import React from "react";
import { FaTachometerAlt, FaUser, FaPlus, FaUsers, FaEnvelope } from "react-icons/fa";
import { NavLink } from "react-router-dom";

const AdminSidebar = () => {
  const sideNav = [
    { label: "Dashboard", icon: <FaTachometerAlt />, path: "/admin/dashboard" },
    { label: "Profile", icon: <FaUser />, path: "/admin/profile" },
    { label: "Create Course", icon: <FaPlus />, path: "/admin/create-course" },
    { label: "View Students", icon: <FaUsers />, path: "/admin/view-students" },
    { label: "Student Messages", icon: <FaEnvelope />, path: "/admin/messages" },
  ];

  return (
   <aside className="fixed top-15 left-0 h-screen w-64 bg-gray-900 text-white flex flex-col shadow-lg overflow-y-auto">
      <div className="text-center py-6 text-2xl font-semibold border-b border-gray-700">
        Admin Panel
      </div>
      <ul className="flex-1 mt-4 space-y-2">
        {sideNav.map((item) => (
          <li key={item.label}>
            <NavLink
              to={item.path}
              className={({ isActive }) =>
                `flex items-center gap-3 px-6 py-3 text-gray-300 hover:bg-gray-800 hover:text-white transition-all ${
                  isActive ? "bg-gray-800 text-white border-l-4 border-blue-500" : ""
                }`
              }
            >
              <span className="text-lg">{item.icon}</span>
              <span className="text-sm font-medium">{item.label}</span>
            </NavLink>
          </li>
        ))}
      </ul>
      <div className="p-4 border-t border-gray-700 text-center text-sm text-gray-400">
        Â© 2025 Bow Valley Admin
      </div>
    </aside>
  );
};

export default AdminSidebar;
